import "timezone"
import "date"

from(bucket: "sensors")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r["_measurement"] == "mqtt_consumer")
  |> filter(fn: (r) => r["topic"] == "beeper/sensors/bme680/all")
  |> filter(fn: (r) => r["_field"] == "temperature")
  |> aggregateWindow(every: v.windowPeriod, fn: mean, createEmpty: false)
  |> map(fn: (r) => {
    // Get time in Eastern timezone
    tz = timezone.location(name: "America/New_York")
    hour = date.hour(t: r._time, location: tz)
    minute = date.minute(t: r._time, location: tz)

    // Calculate if lights should be ON (6:30 AM - 7:00 PM)
    minutesSinceMidnight = hour * 60 + minute
    lightsOn = minutesSinceMidnight >= 390 and minutesSinceMidnight < 1140

    return {r with _value: if lightsOn then 1.0 else 0.0, _field: "Lights (0=OFF, 1=ON)"}
  })

