from(bucket: "sensors")
  |> range(start: -5m)
  |> filter(fn: (r) => r["_measurement"] == "mqtt_consumer")
  |> filter(fn: (r) => r["topic"] == "beeper/camera/csi/metadata")
  |> filter(fn: (r) => r["_field"] == "Lux")
  |> aggregateWindow(every: 30s, fn: mean, createEmpty: false)
  |> map(fn: (r) => {
    // Dynamic lights detection based on actual lux reading
    // Threshold: 80 lux (midpoint between OFF ~30 lux and ON ~140 lux)
    // Based on 7-day analysis: OFF < 50 lux, ON >= 100 lux
    lightsOn = r._value > 80.0

    return {r with _value: if lightsOn then 1.0 else 0.0, _field: "Lights"}
  })
  |> last()
